import './app.css';

import { Route, useLocation } from "react-router-dom";
import { Landing, Form, Home, Detail } from "./views";
import NavBar from "./components/NavBar/NavBar";

function App() {

  const location = useLocation();

  
  return (
    <div className="app">

      {location.pathname !== "/" && <NavBar />}

      <Route exact path="/">
        <Landing />
      </Route>

      <Route path="/home">
        <Home />
      </Route>

      <Route path="/form">
        <Form />
      </Route>

      <Route path="/detail">
        <Detail />
      </Route>
    </div>
  );
}

export default App;
