const Validation = (newpokemon) => {

    const errors = {};

    if (!newpokemon.nombre) {
        return errors.nombre = 'Colócale un nombre a tu pokemon'
    }
    if (newpokemon.nombre !== String) {
        return errors.nombre = 'El nombre no puede contener numeros'
    }
    if (newpokemon.nombre.length > 20) {
        return errors.nombre = 'eE nombre no puede superar los 20 caracteres'
    }
} 

export default Validation;
