import { GET_POKEMONS, BACK_PAGE ,NEXT_PAGE , filter_POKEMOns, order_POKEMONs } from "./actionsType";
const initialState = {
    pokemons: [],
    pokemonsFyO:[], 
    page: 1
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONS:
            return { ...state, 
                pokemons: action.payload ,
                pokemonsFyO: action.payload
            };

        case NEXT_PAGE:
            return { ...state, page: state.page + 1 };
        case BACK_PAGE:
            return { ...state, page: state.page - 1 };


        default:
            return { ...state };
    }
};

export default rootReducer;
