import axios from "axios";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";


const Detail = () => {

    const { id } = useParams();
    const [pokemon, setPokemon] = useState({});

    useEffect(() => {
        axios(`http://localhost:3001/pokemon/get/${id}`)
            .then(response => response.data)
            .then((data) => {
                if (data.name) {
                    setCharacter(data);
                } else {
                    window.alert('No hay personajes con ese ID');
                }
            });
        return setCharacter({});
    }, [id]);
    // useEffect(async () => {

    //     const response =  axios(`http://localhost:3001/pokemon/get/${id}`).then
    //     const pokemonFound = response.data;
    //     console.log(pokemonFound);

    //     setPokemon(pokemonFound);

    // }, [id]);


    return (
        <>
            <hr></hr>
            <h1> DETAILL </h1>
            <h4>detalle del pokemon con id: {id}</h4>
            <hr></hr>
        </>
    )
}

export default Detail;
