import { GET_POKEMONSbdd, GET_POKEMONSapi } from "./actionsType";

const initialState = {
    pokemons: []
};

const rootReducer = (state = initialState, { type, payload }) => {
    switch (type) {

        case GET_POKEMONSapi:
            return { ...state, pokemons: payload };
        default:
            return { ...state };
    }
};

export default rootReducer;
