import { GET_POKEMONSbdd, GET_POKEMONSapi } from "./actionsType";

const initialState = {
    pokemonsApi: [],
    pokemonsBdd: []
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONSbdd:
            return { ...state, pokemonsBdd: action.payload };
        case GET_POKEMONSapi:
            return { ...state, pokemonsApi: action.payload };
        default:
            return { ...state };
    }
};

export default rootReducer;
