import { GET_POKEMONS, BACK_PAGE, NEXT_PAGE, filter_POKEMOns, order_POKEMONs } from "./actionsType";
const initialState = {
    pokemons: [],
    pokemonsFyO: [],
    page: 1
};

const rootReducer = (state = initialState, {type , payload}) => {
    switch (type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: payload,
                pokemonsFyO: payload
            };

        case NEXT_PAGE:
            return { ...state, page: state.page + 1 };
        case BACK_PAGE:
            return { ...state, page: state.page - 1 };

        // case filter_POKEMOns:
            
        //     return { ...state, pokemonsFyO: payload };

        case order_POKEMONs:
            const orderPokemons = [... state.pokemonsFyO]
            return {
                ...state,
                pokemonsFyO:
                    payload === "A"
                    ? orderPokemons.sort((a,b)=> a.ataque - b.ataque)
                    : orderPokemons.sort((a,b)=> b.ataque - a.ataque)
                    
            };


        default:
            return { ...state };
    }
};

export default rootReducer;
