import { GET_POKEMONS, BACK_PAGE, NEXT_PAGE, filter_POKEMOns, order_POKEMONs, BY_BASE_LOCAL, GET_POKEMON_id } from "./actionsType";
const initialState = {
    aPokemon:{},
    pokemons: [],
    pokemonsFyO: [],
    page: 1
};

const rootReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: payload,
                pokemonsFyO: payload
            };

        case NEXT_PAGE:
            return { ...state, page: state.page + 1 };
        case BACK_PAGE:
            return { ...state, page: state.page - 1 };

        case filter_POKEMOns:
            return { ...state, pokemonsFyO: payload };

        case order_POKEMONs:
            const orderPokemons = [...state.pokemonsFyO]
            return {
                ...state,
                pokemonsFyO:
                    payload === "A"
                        ? orderPokemons.sort((a, b) => a.ataque - b.ataque)
                        : orderPokemons.sort((a, b) => b.ataque - a.ataque)
            };


        case BY_BASE_LOCAL:
            if (payload === "base") {
                return {
                    ...state,
                    pokemonsFyO: state.pokemons.filter(poke => poke.id.length > 15)
                };
            }
            if (payload === "api") {

                const filtId = state.pokemons.filter(poke => poke.id < 300);
                return {
                    ...state,
                    pokemonsFyO: [...filtId]
                };
            } if(payload === "todos") {
                return {
                    ...state,
                    pokemonsFyO: [...state.pokemons]
                }
            }
            case GET_POKEMON_id:
            return { ...state, 
                aPokemon: payload };


        default:
            return { ...state };
    }
};

export default rootReducer;
