import axios from 'axios';

import { GET_POKEMONSbdd, GET_POKEMONSapi , NEXT_PAGE, BACK_PAGE } from './actionsType';

export const getPokemonsbdd = () =>{
    return async (dispatch) => {
        const databdd = await axios.get("http://localhost:3001/pokemon/get");
        const pokemons = databdd.data;

        dispatch({type: GET_POKEMONSbdd, payload: pokemons});
    };
};
export const getPokemonsapi = () =>{
    return async (dispatch) => {
        const dataapi = await axios.get("http://localhost:3001/pokemonapi/get/");
        const pokemons = dataapi.data;

        dispatch({type: GET_POKEMONSapi, payload: pokemons});
    };
};

export const nextPage = () =>{
    return () => {
       
        dispatch({type: NEXT_PAGE});
    };
};
export const backPage = () =>{
    return () => {
       
        dispatch({type: BACK_PAGE});
    };
};
