import { Link } from "react-router-dom"
import { useState } from "react";
import Validation from './../../components/Validations/Validations'
import { useDispatch } from "react-redux";

const Form = () => {

    const dispatch = useDispatch();
    
    const [errors, setErrors] = useState({})

    const [newPokemon, setNewPokemon] = useState({
        nombre: '',
        vida: '',
        ataque: '',
        velocidad: '',
        altura: '',
        peso: '',
        tipos: []

    });

    const handleChange = (event) => {

        setNewPokemon({
            ...newPokemon,
            [event.target.name]: event.target.value
        })
        setErrors(Validation({

            ...newPokemon,
            [event.target.name]: event.target.value
        }))
    }
    const createPokemon = (pokemon) => {
        // importar la action createpokemon 
        dispatch(createPokemon(pokemon))
    }


    return (
        <>
            <Link to="home"> volver ####</Link>

            <h1> FORM  </h1>
            <h4>agg pokemon</h4>

            <label htmlFor="nombre">nombre</label>
            <input onChange={handleChange} type="text" name="nombre" />
            {errors.nombre && <p>{errors.nombre}</p>}


            <label htmlFor="vida">vida</label>
            <input onChange={handleChange} type="text" name="vida" />

            <label htmlFor="ataque">ataque</label>
            <input onChange={handleChange} type="text" name="ataque" />

            <label htmlFor="velocidad">velocidad</label>
            <input onChange={handleChange} type="text" name="velocidad" />

            <label htmlFor="altura">altura</label>
            <input onChange={handleChange} type="text" name="altura" />

            <label htmlFor="peso">peso</label>
            <input onChange={handleChange} type="text" name="peso" />

            <label htmlFor="tipos">tipos</label>
            <input onChange={handleChange} type="text" name="tipos" />

            <button>Crear</button>
        </>
    )
}

export default Form;
