import { GET_POKEMONS, BACK_PAGE, NEXT_PAGE, filter_POKEMOns, order_POKEMONs } from "./actionsType";
const initialState = {
    pokemons: [],
    pokemonsFyO: [],
    page: 1
};

const rootReducer = (state = initialState, {type , payload}) => {
    switch (action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                pokemonsFyO: action.payload
            };

        case NEXT_PAGE:
            return { ...state, page: state.page + 1 };
        case BACK_PAGE:
            return { ...state, page: state.page - 1 };

        case filter_POKEMOns:
            const filterPokemons = state.pokemons.filter(poke => poke.type === action.payload)
            return { ...state, pokemonsFyO: filterPokemons };

        case order_POKEMONs:
            const orderPokemons = [... state.poke.ataquemons]
            return {
                ...state,
                pokemonsFyO:
                    action.payload === "A"
                    ? orderPokemons.sort((a,b)=>a.ataque-b.ataque)
                    : orderPokemons.sort((a,b)=>b.ataque-a.ataque)
                    
            };


        default:
            return { ...state };
    }
};

export default rootReducer;
