import { GET_POKEMONSbdd, GET_POKEMONSapi, BACK_PAGE ,NEXT_PAGE } from "./actionsType";
const initialState = {
    pokemons: [],
    page: 1
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONSbdd:
            return { ...state, pokemons: action.payload };
        case GET_POKEMONSapi:
            return { ...state, pokemons: action.payload };
        case NEXT_PAGE:
            return { ...state, page: + 1 };
        case BACK_PAGE:
            return { ...state, page: - 1 };


        default:
            return { ...state };
    }
};

export default rootReducer;
