const Validation = (newpokemon) => {

    const errors = {};

    if (!newpokemon.nombre) {
         errors.nombre = 'Colócale un nombre a tu pokemon'
    }
    if (newpokemon.nombre !== String) {
         errors.nombre = 'El nombre no puede contener numeros'
    }
    if (newpokemon.nombre.length > 20) {
         errors.nombre = 'eE nombre no puede superar los 20 caracteres'
    }

    return errors;
} 

export default Validation;
